$(document).ready(function(){(function($){"use strict";$(function(){$('#contactForm').validate({rules:{fname:{required:true,minlength:2},guest:{required:true},attending:{required:true},email:{required:true,email:true},message:{required:true,minlength:10}},messages:{fname:{required:"Please enter your name",minlength:jQuery.validator.format("At least {0} characters required!")},email:"Please enter your email",guest:"Please select number of guest",attend:"Please select event",message:{maxlength:jQuery.validator.format("Please enter no more than {0} characters!")},},submitHandler:function(form){$(form).ajaxSubmit({type:"POST",data:$(form).serialize(),url:"mailer.php",success:function(){$('#contactForm :input').attr('disabled','disabled');$('#contactForm').fadeTo("slow",1,function(){$(this).find(':input').attr('disabled','disabled');$(this).find('label').css('cursor','default');$('#success').fadeIn();$('.modal').modal('hide');$('#success').modal('show');})},error:function(){$('#contactForm').fadeTo("slow",1,function(){$('#error').fadeIn(); $('.modal').modal('hide');$('#error').modal('show');})}})}})})})(jQuery)})